			and suffix=#{suffix}


			and validStatus = #{validStatus}

select * from image_main


		uuid=#{uuid}


		and md5=#{md5}


		and suffix=#{suffix}


		and validStatus = #{validStatus}



order by updateTime desc
limit #{offset},#{pageSize}
